import React, { useState } from "react";
import Table from "./Table";

const Contact = () => {
  let [userName, setUsername] = useState();
  let [userEmail, setUserEmail] = useState();
  let [userContact, setUserContact] = useState();
  let [num, setNum] = useState();
  let [data, setData] = useState({
    name: "",
    email: "",
    contact: "",
  });

  let updateData = (e) => {
    let { name, value } = e.target;
    setData((preData) => {
      return {
        ...preData,
        [name]: value,
      };
    });
  };

  let storeData = (e) => {
    e.preventDefault();
    if (data.name.length < 3 || data.name.length == undefined) {
      alert("value to short for Name field");
    }
    if (data.contact.length < 10) {
      alert("Please Fill the correct contact details");
    }
    setUsername(data.name)
  };
  return (
    <>
      <h1 className="text-center display-3" style={{ color: "#e74c3c" }}>
        Login Details
      </h1>
      <div className="container w-50 mx-auto mt-3">
        <form onSubmit={storeData}>
          <div className="mb-3">
            <label for="exampleInputPassword1" className="form-label">
              Name
            </label>
            <input
              type="text"
              className="form-control"
              id="name"
              placeholder="Enter Your Name"
              value={data.name}
              onChange={updateData}
              name="name"
            />
          </div>

          <div className="mb-3">
            <label for="exampleInputEmail1" className="form-label">
              Email address
            </label>
            <input
              type="email"
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
              placeholder="Enter Your E-mail"
              value={data.email}
              onChange={updateData}
              name="email"
            />
            <div id="emailHelp" className="form-text">
              We'll never share your email with anyone else.
            </div>
          </div>
          <div className="mb-3">
            <label for="exampleInputPassword1" className="form-label">
              Contact
            </label>
            <input
              type="number"
              className="form-control"
              id="exampleInputPassword1"
              placeholder="Enter Your Contact"
              value={data.contact}
              onChange={updateData}
              name="contact"
            />
          </div>

          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>

      <Table
        nameValue={userName}
        emailValue={userEmail}
        contactValue={userContact}
        num={num}
      />
    </>
  );
};

export default Contact;
